function PopupWithForm({name, title, isOpen, onClose, children, buttonText, onSubmit}) {

    return (
        <div className= {`popup_type_${name} popup ${isOpen ? 'popup__visible' : ''} `}> 
                <div className="popup__container">
                    <button  type ="button" aria-label="close" onClick={onClose}
                        className="popup__close">
                    </button>
                    <h2 className="popup__title">{title}</h2>
                    <form name ={`${name}`} className="popup__container-input" onSubmit={onSubmit}>
                        {children}
                        <button type ="submit" aria-label="saveButton"
                            className="popup__save">{buttonText}
                        </button>
                    </form>
                </div>
        </div>
    );
}



export default PopupWithForm;
