import PopupWithForm from './PopupWithForm';
import {useState, useRef, useEffect} from 'react';

function EditAvatarPopup({isOpen, onClose, onUpdateAvatar, buttonText}) {
    let avatarRef = useRef(null); 

    function handleSubmit(e) {
        // Запрещаем браузеру переходить по адресу формы
        e.preventDefault();

        // Передаём значения управляемых компонентов во внешний обработчик
        onUpdateAvatar({
            avatar: avatarRef.current.value,
        });
        //e.target.reset();
    }

    useEffect(() => {
        if (isOpen) {
            avatarRef.current.value = '';
        }
    }, [isOpen])

    return (
        <PopupWithForm 
            name = {'change-avatar'} title = {'Обновить аватар'}
            isOpen = {isOpen} onClose = {onClose}
            buttonText = {buttonText} onSubmit={handleSubmit}
        >
            <div className="popup__item-container">
                <input 
                ref={avatarRef}
                required type="url" name="avatar"
                placeholder="Ссылка на аватар" className="popup__item popup__item_text_linkAvatar"
            />
            </div>  
        </PopupWithForm>

    );
}

export default EditAvatarPopup;
