import Card from './Card';
import CurrentUserContext from '../contexts/CurrentUserContext';
import {useContext} from 'react';

function Main(
    {handleEditAvatarClick, handleEditProfileClick,
    handleAddPlaceClick, onCardClick, onCardLike, onCardDelete, cards}) {

    const currentUser = useContext(CurrentUserContext);

    return (
    <main class="main">
         <section className="profile">
            <img
            className="profile__avatar"
            src={currentUser.avatar}
            alt="Картинка аватар"
            />
          <button className="profile__avatar-btn" onClick={handleEditAvatarClick}></button>
          <div className="profile__info">
            <h1 className="profile__title">{currentUser.name}</h1>
            <button type="button" className="profile__edit" onClick={handleEditProfileClick}></button>
            <p  className="profile__subtitle">{currentUser.about}</p>
          </div>
          <button className="profile__add" type="button" onClick={handleAddPlaceClick}> 
            <img
              className="profile__plus"
              src="<%=require('./images/Plas.svg')%>"
              alt="Картинка плюс"
            />
          </button>
        </section>
        <section className="elements">
            {cards.map (card => {
                return <Card 
                onCardClick = {onCardClick} onCardLike = {onCardLike}
                onCardDelete ={onCardDelete} card={card} key = {card._id}/>
                })}
        </section>
      </main> 



    );
}

export default Main;
